sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

isDatePast: function(strDate){
    // Create GlideDateTime objects for the current date and the passed in date
    var rightnow = new GlideDateTime();
    var testdate = new GlideDateTime(strDate);

    // If the testdate is before rightnow, return true, else return false
    if (testdate.before(rightnow)) {
      return true;
    }
    else {
      return false;
    }
  },
	/* SFIDA */
	isDateToday : function(strDate) {
		// Creo un oggetto GlideDateTime per la data odierna e la data passata come parametro
		var dataOdierna = new GlideDateTime().getLocalDate();
		var dataTest = new GlideDateTime(strDate).getLocalDate();

		// Dichiarazione e inizializzazione variabile di appoggio
		var flag = false; // variabile che indica se il test è vero o falso

		// controllo se la data passata come parametro è quella di oggi
		if(dataOdierna.compareTo(dataTest) == 0) {

			flag = true; // le date confrontate sono uguali
		}

		return flag;
	},

    type: 'NeedItUtils'
};
