sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({
    api,
    event,
    helpers,
    imports
}) {

    if (event.payload.pageSize) {
        api.setState('niPageSize', () => event.payload.pageSize);
    }
    if (event.payload.pageCursor) {
        api.setState('niPageCursor', () => event.payload.pageCursor);
    }
    if (event.payload.pageNumber) {
        api.setState('niPageNumber', () => event.payload.pageNumber);
    }
}
